import axios from "axios"
import react from "react"
import { useNavigate, useState } from "react-router-dom"

import useAuth from "../../hooks/useAuth"
import useCustomForm from "../../hooks/useCustomForm"



  
  
  // const [, setText] = useState([""]);

  
      let newComment = 
        {
            "video_id": "",
            "text": "",
            "likes": 0,
            "dislikes": 0
        }
        
        const CommentForm = (props) => {
            const [user, token] = useAuth()
            // const [text, setText] = useState([""]);
            const navigate = useNavigate()
            const [formData, handleInputChange, handleSubmit] = useCustomForm(newComment, postNewComment)
        
            async function postNewComment(){
                try {
                    let response = await axios.post("http://127.0.0.1:8000/api/comment/", formData, {
                        headers: {
                            Authorization: 'Bearer ' + token
                        }
                    })
                    navigate("/")
                } catch (error) {
                    
                }
            }   

     
     
  

  
  

  return (
  <div>
    <h2>{user.username}</h2>
    <form onSubmit={handleSubmit}>
      <label style={{'padding':'1em'}}>
        Comment
        <input type="text" value={formData.text} onChange={handleInputChange} />
      </label>
      <input type="submit" value="text" />
    </form>
  </div>
)};
;

export default CommentForm
