import React, { useContext, useEffect, useState } from "react";
import AuthContext from "../../context/AuthContext";
import useCustomForm from "../../hooks/useCustomForm";
import "./LoginPage.css";


// LoginPage.js


function LoginPage() {

  const { loginUser, isServerError } = useContext(AuthContext);
  const defaultValues = { username: "", password: "" };
  const [formData, handleInputChange, handleSubmit, reset] = useCustomForm(
    defaultValues,
    loginUser
  );

  useEffect(() => {
    if (isServerError) {
      reset();
    }
  }, [isServerError]);

  return (
    <div className="bg-primary-subtle min-vh-100">
      <h2 className="container py-5 mb-5">Welcome to Project Data</h2>
      <form className="form" onSubmit={handleSubmit}>
        <div>
          <label className="py-2 me-2">Username:</label>
          <input type="text" name="username" value={formData.username} onChange={handleInputChange} />
        </div>
        <div>
          <label className="me-3 py-2">Password:</label>
          <input type="text" name="password" value={formData.password} onChange={handleInputChange} />
        </div>
        <button className="alert alert-primary" role="alert" class="btn btn-light mt-3"type="button">Login</button>
      </form>
    </div>
  );
}

export default LoginPage;
