import React, { useContext, useEffect, useState } from "react";
import AuthContext from "../../context/AuthContext";
import useCustomForm from "../../hooks/useCustomForm";
import "./LoginPage.css";


// LoginPage.js


function LoginPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = (event) => {
    event.preventDefault();
    // Perform login authentication
    if (username === 'admin' && password === 'password') {
      // alert('Login successful!');
    } else {
      // alert('Invalid username or password. Please try again.');
    }
  };

  const alertPlaceholder = document.getElementById('liveAlertPlaceholder')
const appendAlert = (message, type) => {
  const wrapper = document.createElement('div')
  wrapper.innerHTML = [
    `<div class="alert alert-${type} alert-dismissible" role="alert">`,
    `   <div>${message}</div>`,
    '   <button type="button" class="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>',
    '</div>'
  ].join('')

  alertPlaceholder.append(wrapper)
}

const alertTrigger = document.getElementById('liveAlertBtn')
if (alertTrigger) {
  alertTrigger.addEventListener('click', () => {
    appendAlert('Nice, you triggered this alert message!', 'success')
  })
}

  return (
    <div className="bg-primary-subtle min-vh-100">
      <h2 className="container py-5 mb-5">Welcome to Project Data</h2>
      <form className="container" onSubmit={handleLogin}>
        <div>
          <label className="py-2 me-2">Username:</label>
          <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} />
        </div>
        <div>
          <label className="me-3 py-2">Password:</label>
          <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
        </div>
        <div id="liveAlertPlaceholder"></div>
        <button class= "btn btn-light mt-3" id="liveAlertBtn" role="alert" type="button">Login</button>
      </form>
    </div>
  );
}

export default LoginPage;
